import React, { useEffect, useState } from "react";
import { Badge, Button, Col, Container, Form, ListGroup, Row } from "react-bootstrap";
import ICard from "../../components/Card";
import Menu from "../../components/Menu";
import Modal from "../../components/Modal";
import styles from "./styles.module.css";
import axios from "axios";
import Header from "../../components/Header";
import { useNavigate, useParams } from "react-router-dom";
import IconTasksOk from '../../utils/icons/task-ok.svg';
import IconTasksWorking from '../../utils/icons/task-working.svg';
const url = 'http://127.0.0.1:3200';

interface ITasks {
    id?: string;
    title?: string;
    status?: boolean;
}

export default function Tasks() {
    const [works, setWorks] = useState([]);
    const [tasks, setTasks] = useState([]);
    const [taskTitle, setTaskTitle] = useState('');
    const [taskDescription, setTaskDescription] = useState('');
    const { work_id } = useParams();

    const handleAllTasks = async () => {
        await axios.get(`${url}/tasks/${work_id}`).then(res => setTasks(res.data.data !== undefined ? res.data.data : []))
    }

    const onChangeTitle = (event: React.ChangeEvent<HTMLInputElement>) => {
        setTaskTitle(event.target.value)
    }

    const onChangeDescription = (event: React.ChangeEvent<HTMLInputElement>) => {
        setTaskDescription(event.target.value)
    }

    const handleSubmit = async (event: React.FormEvent<HTMLFormElement>) => {
        event.preventDefault();
        const task = {
            title: taskTitle,
            description: taskDescription,
            work_id
        }

        const headers = {
            headers: {
                'Content-Type': 'application/json'
            }
        }

        await axios.post(`${url}/tasks`, task, headers).then(res => {
            console.log(res)
            handleAllTasks()
        }).catch((error) => {
            console.log(error)
        })
    }

    useEffect(() => {
        handleAllTasks()
    }, [])
    return (
        <>
            <Menu />
            <div className={styles.home}>
                <Container>
                    <Header title="Tarefas" subtitle="Lista com Todas as tarefas do Trabalho" />
                </Container>
            </div>

            <div className={styles.works}>
                <Container>
                    <Row>

                        <Col md={12} className={styles.tasks}>
                            <Row className={styles.tasks_header}>
                                <Col md={12} className="d-flex justify-content-end">
                                    <Modal title="Adicionar Tarefa">
                                        <Form onSubmit={handleSubmit}>
                                            <Form.Group className="mb-3">
                                                <Form.Label>Titulo</Form.Label>
                                                <Form.Control type="text" placeholder="Digite o Título da Tarefa" value={taskTitle} onChange={onChangeTitle} />
                                            </Form.Group>

                                            <Form.Group className="mb-3" controlId="description">
                                                <Form.Label>Descrição</Form.Label>
                                                <Form.Control as="textarea" rows={3} onChange={onChangeDescription} />
                                            </Form.Group>

                                            <Button variant="primary" type="submit" style={{ marginRight: 10 }}>
                                                Adicionar
                                            </Button>

                                            <Button variant="danger">
                                                Cancelar
                                            </Button>
                                        </Form>
                                    </Modal>

                                </Col>
                            </Row>

                            <Row>

                                {
                                    tasks.map((task: ITasks) =>
                                        <ListGroup>
                                            <ListGroup.Item as="li"
                                                className="d-flex justify-content-between align-items-start"
                                                style={{ marginLeft: 10, marginBottom: 10, padding: 10 }}
                                                variant={`${task.status == true ? 'success' : ''}`}
                                            >
                                                <div className="ms-2 me-auto">
                                                    <img src={`${task.status === true ? IconTasksOk : IconTasksWorking} `} width={30} className="rounded-circle" />
                                                    <span className={styles.taskTitle}>{task.title}</span>
                                                </div>

                                                <Badge bg="Link" pill>
                                                    <Button className="btn btn-warning">

                                                        {
                                                            task.status === false ? (
                                                                <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-check-circle" viewBox="0 0 16 16">
                                                                    <path d="M8 15A7 7 0 1 1 8 1a7 7 0 0 1 0 14zm0 1A8 8 0 1 0 8 0a8 8 0 0 0 0 16z" />
                                                                    <path d="M10.97 4.97a.235.235 0 0 0-.02.022L7.477 9.417 5.384 7.323a.75.75 0 0 0-1.06 1.06L6.97 11.03a.75.75 0 0 0 1.079-.02l3.992-4.99a.75.75 0 0 0-1.071-1.05z" />
                                                                </svg>
                                                            ) :
                                                                <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-circle-fill" viewBox="0 0 16 16">
                                                                    <circle cx="8" cy="8" r="8" />
                                                                </svg>
                                                        }
                                                    </Button>

                                                    <Button className="btn btn-danger" style={{ marginRight: 5, marginLeft: 10 }}>
                                                        <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-trash" viewBox="0 0 16 16">
                                                            <path d="M5.5 5.5A.5.5 0 0 1 6 6v6a.5.5 0 0 1-1 0V6a.5.5 0 0 1 .5-.5zm2.5 0a.5.5 0 0 1 .5.5v6a.5.5 0 0 1-1 0V6a.5.5 0 0 1 .5-.5zm3 .5a.5.5 0 0 0-1 0v6a.5.5 0 0 0 1 0V6z" />
                                                            <path fillRule="evenodd" d="M14.5 3a1 1 0 0 1-1 1H13v9a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2V4h-.5a1 1 0 0 1-1-1V2a1 1 0 0 1 1-1H6a1 1 0 0 1 1-1h2a1 1 0 0 1 1 1h3.5a1 1 0 0 1 1 1v1zM4.118 4 4 4.059V13a1 1 0 0 0 1 1h6a1 1 0 0 0 1-1V4.059L11.882 4H4.118zM2.5 3V2h11v1h-11z" />
                                                        </svg>
                                                    </Button>
                                                </Badge>
                                            </ListGroup.Item>

                                        </ListGroup>
                                    )
                                }
                            </Row>

                        </Col>
                    </Row>
                </Container>
            </div>
        </>
    )
}
