import { useState } from 'react';
import { ToastContainer } from 'react-bootstrap';
import Toast from 'react-bootstrap/Toast';

interface IProps {
 icon?: string;
 title: string;
 status: string;
}

export default function Message({icon, title, status}: IProps) {
    const [show, setShow] = useState(false);
  return (
      <ToastContainer position='top-end' style={{ marginTop: 15}}>
    <Toast className={`btn ${status === 'error' ? 'btn-danger' : 'btn-info' }`} show={true} delay={200} onClick={() => setShow(false)} autohide>
      <Toast.Body>{title}</Toast.Body>
    </Toast>

      </ToastContainer>
  );
}
