import { useContext, useEffect, useState } from "react";
import { Button, Col, Container, Form, Offcanvas, Row } from "react-bootstrap";
import ICard from "../../components/Card";
import Menu from "../../components/Menu";
import Modal from "../../components/Modal";
import styles from "./styles.module.css";
import axios from "axios";
import Header from "../../components/Header";
import { Link } from "react-router-dom";
import { AuthContext } from "../../Contexts/Auth/AuthContext";
import { getToken } from "../../hooks/localstorage";
import { getAllWorks } from "./service.module";
const url = 'http://127.0.0.1:3200';

interface IWork {
    id?: string;
    title?: string;
    description?: string;
    status: boolean;
    qtd_task: number;
}

export default function Home() {
    const [works, setWorks] = useState([]);
    const [tasks, setTasks] = useState([]);
    const [showInfo, setShowInfo] = useState(false);
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const user = useContext(AuthContext);

    const handleColse = () => setShowInfo(false);

    const handleWorks = async () => {
        const works = await getAllWorks(user.user?.id).then(res => {
            return res.data
        })

        setWorks(works);
    }

    const handleTasks = async (id: string) => {
        const token = await getToken();
        const headers = {
            headers: {'Authorization': `Bearer ${token}` }
        }
        await axios.get(`${url}/tasks/${id}`, headers).then(res => setTasks(res.data.data))
    }

    const handleSubmit = () => {
        alert('funfando')
    }

    useEffect(() => {
        handleWorks()
    }, [])
    return (
        <>
            <Menu />
            <div className={styles.home}>
                <Container>
                   <Header title="Trabalhos" subtitle="Confira aqui todos os trabalhos adicionados até agora" />
                </Container>
            </div>

            <div className={styles.works}>
                <Container className={styles.container}>
                    <Row>
                        <Col className="d-flex flex-row-reverse bd-highlight">

                        <Modal title="Novo Trabalho">
                                        <Form onSubmit={handleSubmit}>
                                            <Form.Group className="mb-3">
                                                <Form.Label>Titulo</Form.Label>
                                                <Form.Control type="text" placeholder="Digite o Título da Tarefa"
                                                    value={title} onChange={() => setTitle}
                                                    required />
                                            </Form.Group>

                                            <Form.Group className="mb-3" controlId="description">
                                                <Form.Label>Descrição</Form.Label>
                                                <Form.Control as="textarea"
                                                    rows={3} onChange={() => setDescription} />
                                            </Form.Group>

                                            <Button variant="primary" type="submit" style={{ marginRight: 10 }}>
                                                Adicionar
                                            </Button>

                                            <Button variant="danger">
                                                Cancelar
                                            </Button>
                                        </Form>
                                    </Modal>
                        </Col>
                    </Row>
                    <Row>
                        {
                            works.map((work: IWork) => 
                                    <Col md={4} className={styles.works} key={work.id}>
                                        <Link to={`/tasks/${work.id}`} style={{ textDecoration: 'none', color: '#000' }}>
                                            <ICard  key={work.id} title={`${work.title}`} text={`${work.qtd_task}`} 
                                                funcao={() => handleTasks(work.id !== undefined ? work.id : '')} /> 
                                        </Link>
                                    </Col>
                            )
                        }
                    </Row>
                </Container>
            </div>
        </>
    )
}
