import axios from "axios";
import { getToken } from "../../hooks/localstorage";
const url = 'http://127.0.0.1:3200';

export const getAllWorks = async (user: string = 'null') => {
    const token = await getToken();
    const headers = {
        headers: { 'Authorization': `Bearer ${token}` }
    }

    await axios.get(`${url}/works/${user}`, headers)
    .then(res => {
        console.log(res)
            return res.data.data !== undefined ? res.data.data : []
        })
        .catch((error) => {
            return (error)
        })
}
