import React, { useEffect, useState } from "react";
import { Badge, Button, Col, Container, Form, ListGroup, Row } from "react-bootstrap";
import ICard from "../../components/Card";
import Menu from "../../components/Menu";
import Modal from "../../components/Modal";
import styles from "./styles.module.css";
import axios from "axios";
import Header from "../../components/Header";
import { useNavigate, useParams } from "react-router-dom";

import IconTasksOk from '../../utils/icons/task-ok.svg';
import IconTasksWorking from '../../utils/icons/task-working.svg';
import IconBtnTaskWorking from '../../utils/icons/btn-task-working.svg';
import IconBtnTaskOk from '../../utils/icons/btn-task-ok.svg';
import IconBtnTaskRemove from '../../utils/icons/btn-task-remove.svg';

const url = 'http://127.0.0.1:3200';

interface ITasks {
    id?: string;
    title?: string;
    status?: boolean;
}

export default function Tasks() {
    const [tasks, setTasks] = useState([]);
    const [taskTitle, setTaskTitle] = useState('');
    const [taskDescription, setTaskDescription] = useState('');
    const [taskCompleate, setTaskCompleate] = useState([]);
    const { work_id } = useParams();

    const handleAllTasks = async () => {
        await axios.get(`${url}/tasks/${work_id}`).then(res =>{ 
            setTasks(res.data.data !== undefined ? res.data.data : [])
        })
    }

    const getTotalTasks = () => {
        const completeTasks = tasks.filter((task: ITasks) => task.status === true )
        setTaskCompleate(completeTasks)
    }

    const onChangeTitle = (event: React.ChangeEvent<HTMLInputElement>) => {
        setTaskTitle(event.target.value)
    }

    const onChangeDescription = (event: React.ChangeEvent<HTMLInputElement>) => {
        setTaskDescription(event.target.value)
    }

    const handleUpdateStatus =  async (task_id: string) => {
        await axios.patch(`${url}/tasks/${task_id}`).then(res => {
            handleAllTasks()
            getTotalTasks()
        }).catch((error) => {
            console.log(error)
        })
    }

    const handleSubmit = async (event: React.FormEvent<HTMLFormElement>) => {
        event.preventDefault();
        const task = {
            title: taskTitle,
            description: taskDescription,
            work_id
        }

        const headers = {
            headers: {
                'Content-Type': 'application/json'
            }
        }

        await axios.post(`${url}/tasks`, task, headers).then(res => {
            console.log(res)
            handleAllTasks()
        }).catch((error) => {
            console.log(error)
        })
    }

    const handleRemoveTask =  async (task_id: string) => {
        await axios.delete(`${url}/tasks/${task_id}`).then(res => {
            console.log(res)
            handleAllTasks()
        }).catch((error) => {
            console.log(error)
        })
    }

    useEffect(() => {
        handleAllTasks()
    }, [])

    useEffect(() => {
        getTotalTasks()
    },[tasks])
    return (
        <>
            <Menu />
            <div className={styles.home}>
                <Container>
                    <Header title="Tarefas" subtitle="Lista com Todas as tarefas do Trabalho" />
                </Container>
            </div>

            <div className={styles.works}>
                <Container>
                    <Row>
                        <Col md={12} className={styles.tasks}>
                            <Row className={styles.tasks_header}>
                                <Col md={6} className="d-flex justify-content-start">
                                <p>{taskCompleate.length}</p>
                                </Col>
                                <Col md={6} className="d-flex justify-content-end">
                                    <Modal title="Adicionar Tarefa">
                                        <Form onSubmit={handleSubmit}>
                                            <Form.Group className="mb-3">
                                                <Form.Label>Titulo</Form.Label>
                                                <Form.Control type="text" placeholder="Digite o Título da Tarefa" value={taskTitle} onChange={onChangeTitle} />
                                            </Form.Group>

                                            <Form.Group className="mb-3" controlId="description">
                                                <Form.Label>Descrição</Form.Label>
                                                <Form.Control as="textarea" rows={3} onChange={onChangeDescription} />
                                            </Form.Group>

                                            <Button variant="primary" type="submit" style={{ marginRight: 10 }}>
                                                Adicionar
                                            </Button>

                                            <Button variant="danger">
                                                Cancelar
                                            </Button>
                                        </Form>
                                    </Modal>

                                </Col>
                            </Row>

                            <Row>
                                {
                                    tasks.length > 0 ? (
                                        tasks.map((task: ITasks) =>
                                            <ListGroup key={task.id}>
                                                <ListGroup.Item as="li"
                                                    className="d-flex justify-content-between align-items-start"
                                                    style={{ marginLeft: 10, marginBottom: 10, padding: 10 }}
                                                    variant={`${task.status == true ? 'success' : ''}`}
                                                >
                                                    <div className="ms-2 me-auto">
                                                        <img src={`${task.status === true ? IconTasksOk : IconTasksWorking} `} width={30} className="rounded-circle" />
                                                        <span className={styles.taskTitle}>{task.title}</span>
                                                    </div>

                                                    <Badge bg="Link" pill>
                                                        <Button className="btn btn-warning" onClick={() => handleUpdateStatus(task.id !== undefined ? task.id : '')}>

                                                            {
                                                                task.status === true ? (
                                                                    <img src={IconBtnTaskOk} />
                                                                ) :
                                                                <img src={IconBtnTaskWorking} />
                                                            }
                                                        </Button>

                                                        <Button className="btn btn-danger" style={{ marginRight: 5, marginLeft: 10 }}
                                                                                           onClick={() => handleRemoveTask(task.id !== undefined ? task.id : '')}>
                                                            <img src={IconBtnTaskRemove} />
                                                        </Button>
                                                    </Badge>
                                                </ListGroup.Item>

                                            </ListGroup>)
                                        ) : 
                                        <h4> Você não possui Tarefas Cadastradas</h4>
                                }
                            </Row>

                        </Col>
                    </Row>
                </Container>
            </div>
        </>
    )
}
