import { useContext, useEffect, useState } from "react";
import { Button, Col, Container, Offcanvas, Row } from "react-bootstrap";
import ICard from "../../components/Card";
import Menu from "../../components/Menu";
import Modal from "../../components/Modal";
import styles from "./styles.module.css";
import axios from "axios";
import Header from "../../components/Header";
import { Link } from "react-router-dom";
import { AuthContext } from "../../Contexts/Auth/AuthContext";
import { getToken } from "../../hooks/localstorage";
const url = 'http://127.0.0.1:3200';

interface IWork {
    id?: string;
    title?: string;
    description?: string;
    status: boolean;
    qtd_task: number;
}

export default function Home() {
    const [works, setWorks] = useState([]);
    const [tasks, setTasks] = useState([]);
    const [showInfo, setShowInfo] = useState(false);
    const user = useContext(AuthContext);

    const handleColse = () => setShowInfo(false);
    const token = getToken();
    const handleWorks = async () => {
        const headers = {
            headers: {'Authorization': `Bearer ${token}` }
        }

        await axios.get(`${url}/works/${ user.user?.id }`, {headers}).
            then(res => {
                setWorks(res.data.data !== undefined ? res.data.data : [])
            })
            .catch((error) => {
                return (error)
            })

    }

    const handleTasks = async (id: string) => {
        await axios.get(`${url}/tasks/${id}`).then(res => setTasks(res.data.data))
    }

    useEffect(() => {
        handleWorks()
    }, [])
    return (
        <>
            <Menu />
            <div className={styles.home}>
                <Container>
                   <Header title="Trabalhos" subtitle="Confira aqui todos os trabalhos adicionados atÃ© agora" />
                </Container>
            </div>

            <div className={styles.works}>
            <Offcanvas show={false} >
        <Offcanvas.Header closeButton>
          <Offcanvas.Title>Nome User</Offcanvas.Title>
        </Offcanvas.Header>
        <Offcanvas.Body>

        </Offcanvas.Body>
      </Offcanvas>
                <Container className={styles.container}>
                    <Row>
                        {
                            works.map((work: IWork) => 
                                    <Col md={4} className={styles.works}>
                                        <Link to={`tasks/${work.id}`} style={{ textDecoration: 'none', color: '#000' }}>
                                            <ICard  key={work.id} title={`${work.title}`} text={`${work.qtd_task}`} 
                                                funcao={() => handleTasks(work.id !== undefined ? work.id : '')} /> 
                                        </Link>
                                    </Col>
                            )
                        }
                    </Row>
                </Container>
            </div>
        </>
    )
}
