import axios from "axios";
import { getToken } from "../../hooks/localstorage";
const url = 'http://127.0.0.1:3200';

export const getAllTasks = async (work_id: string = '') => {
    const token = await getToken();
    const headers = {
        headers: { 'Authorization': `Bearer ${token}` }
    }

    const data = await axios.get(`${url}/tasks/${work_id}`, headers)
    .then(res => {
        return res.data
    }).catch(error => {
        return error
    })

    return data;
}

export const completeTask = async (status: boolean) => {
    if(status === false) {
        await axios.patch(`${URL}/tasks/${task_id}`).then(res => {
            handleAllTasks()
            getTotalTasks()
            validateMessage(res.data.message,'success',true);
        }).catch((error) => {
            validateMessage("Não foi possível Concluir a Tarefa",'danger',true);
        })
    }
}
